import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { BrowserHistory } from 'react-router';
import { Modal } from 'react-bootstrap';
import Navbar from '../../components/Navbar';
import { loadResults } from '../../loadResults';
import Results from '../Results/index';
import './styles.css';

const Landing = () => {
  let history = useHistory();
  const [firstName, setFirstName] = useState('');
  const [destCity, setDestCity] = useState('');
  const [arriveDate, setArriveDate] = useState('');
  const [numberNights, setNumberNights] = useState('');
  const [questionNumber, setQuestionNumber] = useState(0);
  const [showModal, setShowModal] = useState(false);

  // Calls loadResults function with all input data, then redirects to results page and sends the response as props
  const handleFinish = async () => {
    setShowModal(true);
    let hotelList = await loadResults({
      firstName,
      destCity,
      arriveDate,
      numberNights,
    });

    history.push({
      pathname: '/results',
      state: { hotelList },
    });
  };

  return (
    <>
      <Navbar />
      <div id='bgvid'>
        <iframe
          frameborder='0'
          height='100%'
          width='100%'
          src='https://youtube.com/embed/FCPdIvXo2rU?autoplay=1&controls=0&showinfo=0&autohide=1&start=15'
          controls='0'
          disabelkb='1'
          start='20'
        ></iframe>
      </div>
      <div className='landing-wrapper'>
        <div className='landing-header-wrapper'>
          <div className='landing-small-header'>welcome to</div>
          <div className='landing-header'>my concierge</div>
        </div>

        <Modal show={showModal}>
          <Modal.Header>
            <Modal.Title>Please wait while we load your results...</Modal.Title>
          </Modal.Header>
        </Modal>
      </div>
    </>
  );
};

export default Landing;
